package com.baosight.wilp.si.web;

import com.baosight.iplat4j.core.ei.EiInfo;
import com.baosight.wilp.si.common.SiUtils;
import org.apache.commons.lang3.StringUtils;
import org.springframework.stereotype.Controller;
import org.springframework.web.bind.annotation.CrossOrigin;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.PathVariable;
import org.springframework.web.bind.annotation.RequestMapping;

import javax.servlet.ServletOutputStream;
import javax.servlet.http.HttpServletResponse;
import javax.xml.bind.DatatypeConverter;
import java.io.ByteArrayOutputStream;
import java.io.IOException;

/**
 * @author fangjian
 * @version V5.0.0
 * @Description: 物资领用通用controller
 * @ClassName: MpCommonController
 * @Package com.baosight.wilp.mp.controller
 * @date: 2022年10月31日 15:42
 *
 * 1.回显电子签名图片
 */
@Controller
@RequestMapping("/si")
public class SiCommonController {

    public static final String IMG_SPE = "base64,";

    /**
     * 根据电子签名编码回显图片
     * @Title: showImg
     * @param fileCode fileCode : fileCode : 电子签名编码
     * @param resp resp
     * @return void
     * @throws
     **/
    @CrossOrigin
    @GetMapping("/showSign/{fileCode}")
    public void showSign(@PathVariable("fileCode") String fileCode, HttpServletResponse resp) throws Exception {
        //获取物资的图片路径
        EiInfo invoke = SiUtils.invoke(null, "XQMS03", "getSignImageByFileCode", new String[]{"fileCode"}, fileCode);
        String imgBase64 = invoke.getString("data");
        //String imgBase64 = "data:image/jpeg;base64,";
        if(imgBase64.contains(IMG_SPE)) {
            imgBase64 = StringUtils.substringAfter(imgBase64, IMG_SPE);
        }

        //图片路径不存在，直接返回
        if(StringUtils.isBlank(imgBase64)) {
            return;
        }
        outputImg(resp, imgBase64);
    }

    /**
     * 将图片base64输出
     * @Title: outputImg
     * @param resp resp
     * @param imgBase64 imgBase64
     * @return void
     * @throws
     **/
    private void outputImg(HttpServletResponse resp, String imgBase64) throws IOException {
        resp.setContentType("image/png;charset=utf-8");
        byte[] decode = DatatypeConverter.parseBase64Binary(imgBase64);
        ByteArrayOutputStream output = null;
        try {
            output=new ByteArrayOutputStream();
            output.write(decode);
            ServletOutputStream out=resp.getOutputStream();
            output.writeTo(out);
        } catch (IOException e1) {
            e1.printStackTrace();
        } finally {
            if(output != null) {output.close();}
        }
    }

}
